mapper.select:
		select ROLE.ROLE_ID,ROLE.ROLE_NAME,ROLE.F_JS,ROLE.F_MX,ROLE.F_PARENT,ROLE.F_NOTE 
          from BSROLE ROLE,BSUSROLE GX
         where ROLE.ROLE_ID = GX.ROLE_ID AND GX.USER_ID = #{USER_ID} 
         order by ROLE.ROLE_ID ;

SELECT BSUSER.USER_ID, BSUSER.USER_NAME, BSUSER.USER_TYPE, BSUSER.USER_ORGID, BSUSER.USER_PASS, BSUSER.USER_DISABLE, BSUSER.USER_ISADMIN, BSUSER.USER_MAIL1, BSUSER.USER_MAIL2, BSUSER.USER_PHONE1, BSUSER.USER_PHONE2, BSUSER.USER_ADDRESSS1, BSUSER.USER_ADDRESSS2, BSUSER.USER_T01, BSUSER.F_CRDATE, BSUSER.F_CHDATE FROM bsuser, bsusrole GX WHERE bsuser.USER_ID = GX.USER_ID
AND bsuser.USER_ORGID like #{USER_ORGID}
AND GX.ROLE_ID = #{ROLE_ID}

SELECT BSUSER.USER_ID, BSUSER.USER_NAME, BSUSER.USER_TYPE, BSUSER.USER_ORGID, BSUSER.USER_PASS, BSUSER.USER_DISABLE, BSUSER.USER_ISADMIN, BSUSER.USER_MAIL1, BSUSER.USER_MAIL2, BSUSER.USER_PHONE1, BSUSER.USER_PHONE2, BSUSER.USER_ADDRESSS1, BSUSER.USER_ADDRESSS2, BSUSER.USER_T01, BSUSER.F_CRDATE, BSUSER.F_CHDATE FROM bsuser where NOT EXISTS (SELECT 1 FROM BSUSROLE GX WHERE bsuser.USER_ID = GX.USER_ID AND GX.ROLE_ID = #{ROLE_ID} )
AND bsuser.USER_ORGID like #{USER_ORGID}
order by bsuser.USER_ID;

mapper.insert:
        INSERT INTO BSUSROLE (
        	USER_ID,
	        ROLE_ID
        ) VALUES (
        	#{USER_ID} ,
        	#{ROLE_ID}
        )
